    <div className="App">
      <h1>Spam Detector</h1>
      <table>
        <thead>
          <tr>
            <th>Label</th>
            <th>Text</th>
          </tr>
        </thead>
        <tbody>
          {emails.map((email, index) => (
            <tr key={index} className={email.label === 'spam' ? 'spam' : 'ham'}>
              <td>{email.label}</td>
              <td>{email.text}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
