import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [emails, setEmails] = useState([]);

  // Carrega os e-mails ao iniciar
  useEffect(() => {
    axios.get('http://127.0.0.1:5000/emails')
      .then(response => {
        setEmails(response.data);
      })
      .catch(error => {
        console.error("Erro ao carregar e-mails:", error);
      });
  }, []);

  // Função para verificar se um e-mail é spam
  const handleCheckSpam = async (emailId, emailText, actualLabel) => {
    try {
      const response = await axios.post('http://127.0.0.1:5000/check-spam', {
        text: emailText,
      });
      const isSpam = response.data.is_spam;

      // Verifica se a previsão foi correta
      const isCorrect = (isSpam && actualLabel === 1) || (!isSpam && actualLabel === 0);

      // Atualiza o e-mail na lista com o resultado
      setEmails(prevEmails =>
        prevEmails.map(email =>
          email.id === emailId
            ? { ...email, is_spam: isSpam, is_correct: isCorrect }
            : email
        )
      );
    } catch (error) {
      console.error("Erro ao verificar spam:", error);
    }
  };

  return (
    <div className="App">
      <header className="header">
        <h1>Detector de Spam</h1>
      </header>
      <div className="content">
        <table>
          <thead>
            <tr>
              <th>E-mail</th>
              <th>Ação</th>
              <th>Resultado</th>
              <th>Acerto</th>
            </tr>
          </thead>
          <tbody>
            {emails.map(email => (
              <tr key={email.id}>
                <td>{email.text}</td>
                <td>
                  <button onClick={() => handleCheckSpam(email.id, email.text, email.label)}>
                    Verificar Spam
                  </button>
                </td>
                <td>
                  {email.is_spam !== undefined && (
                    <span className={email.is_spam ? 'spam' : 'ham'}>
                      {email.is_spam ? 'Spam' : 'Não é Spam'}
                    </span>
                  )}
                </td>
                <td>
                  {email.is_correct !== undefined && (
                    <span className={email.is_correct ? 'correct' : 'incorrect'}>
                      {email.is_correct ? '✔️ Correto' : '❌ Errado'}
                    </span>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
