import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [emails, setEmails] = useState([]);

  // Carrega os e-mails ao iniciar
  useEffect(() => {
    axios.get('http://127.0.0.1:5000/emails')
      .then(response => {
        setEmails(response.data);
      })
      .catch(error => {
        console.error("Erro ao carregar e-mails:", error);
      });
  }, []);

  // Função para verificar se um e-mail é spam
  const handleCheckSpam = async (emailId, emailText) => {
    try {
      const response = await axios.post('http://127.0.0.1:5000/check-spam', {
        text: emailText,
      });
      const isSpam = response.data.is_spam;

      // Atualiza o e-mail na lista com o resultado
      setEmails(prevEmails =>
        prevEmails.map(email =>
          email.id === emailId ? { ...email, is_spam: isSpam } : email
        )
      );
    } catch (error) {
      console.error("Erro ao verificar spam:", error);
    }
  };

  return (
    <div className="App">
      <h1>Lista de E-mails</h1>
      <table>
        <thead>
          <tr>
            <th>E-mail</th>
            <th>Ação</th>
            <th>Resultado</th>
          </tr>
        </thead>
        <tbody>
          {emails.map(email => (
            <tr key={email.id}>
              <td>{email.text}</td>
              <td>
                <button onClick={() => handleCheckSpam(email.id, email.text)}>
                  Verificar Spam
                </button>
              </td>
              <td>
                {email.is_spam !== undefined && (
                  <span className={email.is_spam ? 'spam' : 'ham'}>
                    {email.is_spam ? 'Spam' : 'Não é Spam'}
                  </span>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
